mapper.select:
SELECT
    *
FROM
    qtProject.[Markings]
WHERE
    dataId = #{dataId}

    AND
    userId = #{userId}
    AND deleted = 0


    AND userId = #{checkedUserId}
    AND deleted = 0


    AND userId = #{checkedUserId}

mapper.update:
UPDATE
    qtProject.[Markings]
SET
    deleted=1, confirmUserId = #{userId}
WHERE
    dataId = #{dataId}
AND
    userId = #{checkedUserId}
AND
    pointId = #{pointId}

UPDATE
    qtProject.[Markings]
SET
    deleted=0, confirmUserId = #{userId}, point =#{point}
WHERE
    dataId = #{dataId}
AND
    userId = #{checkedUserId}
AND
    pointId = #{pointId}

mapper.delete:
DELETE
FROM
    qtProject.[Markings]
WHERE
    dataId = #{dataId}
AND
    userId = #{userId}
AND
    pointId = #{pointId}
