mapper.select:
SELECT
    *
FROM
    qtProject.[Markings]
WHERE
    dataId = #{dataId}

    AND
    userId = #{userId}
    AND deleted = 0


    AND
    userId = #{userId}
    AND deleted = 0


    AND userId = #{checkedUserId}

mapper.delete:
DELETE
FROM
    qtProject.[ConfirmMarkings]
WHERE
    dataId = #{dataId}
AND
    userId = #{userId}
AND
    pointId = #{pointId}

mapper.update:
UPDATE
    qtProject.[Markings]
SET
    deleted=0, confirmUserId = #{userId}, point =#{point}
WHERE
    dataId = #{dataId}
AND
    pointId = #{pointId}

